p.c:
class DefaultSearchGoal extends Cplex.Goal {
  public Cplex.Goal execute(Cplex cplex) throws ILOG.Concert.Exception {
    if (!isIntegerFeasible())
      return cplex.And(cplex.BranchAsCplex(), this);
    return null;
  }
}

